import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';
import Header_nav from './components/Header/Header_nav';
import Header_intro from './components/Header/Header_intro';
import Service_intro from './components/Contents/Service_intro';
import Footer from './components/Footer/Footer';
import Dashboard from './components/Contents/Dashboard';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => {
    setIsLoggedIn(true); // 고정된 사용자 데이터를 활용하여 로그인 상태 변경
  };

  return (
    <Router>
      <div className="App">
        <Header_nav />
        <Routes>
          {!isLoggedIn ? (
            <>
              <Route 
                path="/" 
                element={<>
                  <Header_intro onLogin={handleLogin} /> 
                  <Service_intro />
                  <Footer />
                </>} 
              />
            </>
          ) : (
            <Route path="/" element={<Dashboard />} />
          )}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
