// Statistics.js
import React, { useState } from "react";
import { Line } from "react-chartjs-2";
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
} from "chart.js";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import "./Statistics.css";

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

function Statistics() {
    const [selectedTab, setSelectedTab] = useState("week");
    const [startDate, setStartDate] = useState(new Date());
    const [endDate, setEndDate] = useState(new Date());
    const [showCalendar, setShowCalendar] = useState(false);
    const [averageTemp, setAverageTemp] = useState("25.6");
    const [selectedMonth, setSelectedMonth] = useState(new Date().getMonth() + 1);
    const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());
    const [hasData, setHasData] = useState(true);

    const handleTabClick = (tab) => {
        setSelectedTab(tab);
        setShowCalendar(false);
        setHasData(tab === "week");
    };

    const handleDateChange = (dates) => {
        const [start, end] = dates;
        setStartDate(start);
        setEndDate(end);
    };

    const weekDays = ['월', '화', '수', '목', '금', '토', '일'];

    const data = {
        labels: weekDays,
        datasets: [{
            data: [20, 25, 28, 24, 22, 23, 30],
            borderColor: '#FF9B9B',
            backgroundColor: 'rgba(255, 155, 155, 0.1)',
            fill: true,
            tension: 0.4,
            pointRadius: 5,
            pointBackgroundColor: ['#4AB7B6', '#A0D995', '#FF9B9B', '#A0D995', '#4AB7B6', '#4AB7B6', '#FF9B9B']
        }]
    };

    const noDataChart = {
        labels: weekDays,
        datasets: [{
            data: Array(7).fill(null),
            borderColor: '#FF9B9B',
            backgroundColor: 'rgba(255, 155, 155, 0.1)',
            fill: true,
            tension: 0.4,
            pointRadius: 0
        }]
    };

    const options = {
        responsive: true,
        maintainAspectRatio: false,
        plugins: {
            legend: {
                display: false
            },
            tooltip: {
                enabled: true,
                callbacks: {
                    label: (context) => `${context.parsed.y}°C`
                }
            }
        },
        scales: {
            x: {
                grid: {
                    display: false,
                    drawBorder: false
                },
                ticks: {
                    color: '#666'
                }
            },
            y: {
                display: false,
                min: 0,
                max: 40,
                grid: {
                    display: false
                }
            }
        },
        layout: {
            padding: 10
        }
    };

    return (
        <div className="statistics-container">
            <header className="statistics-header">
                <h1>나의 온도 평균</h1>
                <p>내가 그동안 기록해왔던 지난 날을 살펴봐요.</p>
            </header>

            <div className="tabs">
                <button
                    className={`tab-button ${selectedTab === "week" ? "active" : ""}`}
                    onClick={() => handleTabClick("week")}
                >
                    주
                </button>
                <button
                    className={`tab-button ${selectedTab === "month" ? "active" : ""}`}
                    onClick={() => handleTabClick("month")}
                >
                    달
                </button>
                <button
                    className={`tab-button ${selectedTab === "year" ? "active" : ""}`}
                    onClick={() => handleTabClick("year")}
                >
                    연도
                </button>
            </div>

            <div className="mood-indicators">
                <div className="mood-item">
                    <span className="mood-emoji">😢</span>
                    <span className="mood-count">: {hasData ? "3" : "-"}</span>
                </div>
                <div className="mood-item">
                    <span className="mood-emoji">😐</span>
                    <span className="mood-count">: {hasData ? "2" : "-"}</span>
                </div>
                <div className="mood-item">
                    <span className="mood-emoji">😊</span>
                    <span className="mood-count">: {hasData ? "2" : "-"}</span>
                </div>
                <button className="date-selector-button" onClick={() => setShowCalendar(!showCalendar)}>
                    날짜 선택 ▼
                </button>
            </div>

            {showCalendar && (
                <div className="calendar-wrapper">
                    <DatePicker
                        selected={startDate}
                        onChange={handleDateChange}
                        startDate={startDate}
                        endDate={endDate}
                        selectsRange
                        inline
                        dateFormat="yyyy.MM.dd"
                    />
                </div>
            )}

            <div className="chart-section">
                {hasData ? (
                    <Line data={data} options={options} />
                ) : (
                    <div className="no-data">정보가 없습니다.</div>
                )}
            </div>

            <div className="result-box">
                <p>
                    {hasData ? (
                        <>
                            <div className="date-range">
                                {startDate && endDate && `${startDate.getFullYear()}.${String(startDate.getMonth() + 1).padStart(2, '0')}.${String(startDate.getDate()).padStart(2, '0')}~${endDate.getFullYear()}.${String(endDate.getMonth() + 1).padStart(2, '0')}.${String(endDate.getDate()).padStart(2, '0')}의`}
                            </div>
                            <div>평균 온도는 <span className="temperature">{averageTemp}°C</span> 입니다.</div>
                        </>
                    ) : (
                        <>
                            <div className="date-range">-</div>
                            <div>평균 온도는 <span className="temperature">---°C</span> 입니다.</div>
                        </>
                    )}
                </p>
            </div>
        </div>
    );
}

export default Statistics;
