import React, { useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import "./Statistics.css";

function Statistics() {
    const [selectedTab, setSelectedTab] = useState("week"); // 주/달/연도 탭
    const [selectedDate, setSelectedDate] = useState(new Date()); // 달력에서 선택한 날짜
    const [averageTemp, setAverageTemp] = useState("25.6°C"); // 온도 데이터

    const handleTabClick = (tab) => {
        setSelectedTab(tab);
    };

    const handleDateChange = (date) => {
        setSelectedDate(date);
    };

    return (
        <div className="statistics-container">
            <header className="statistics-header">
                <h1>나의 온도 평균</h1>
                <p>내가 그동안 기록해왔던 지난 날을 살펴봐요.</p>
            </header>

            <div className="tabs">
                <button
                    className={`tab-button ${selectedTab === "week" ? "active" : ""}`}
                    onClick={() => handleTabClick("week")}
                >
                    주
                </button>
                <button
                    className={`tab-button ${selectedTab === "month" ? "active" : ""}`}
                    onClick={() => handleTabClick("month")}
                >
                    달
                </button>
                <button
                    className={`tab-button ${selectedTab === "year" ? "active" : ""}`}
                    onClick={() => handleTabClick("year")}
                >
                    연도
                </button>
            </div>

            <div className="date-selector">
                <label>날짜 선택:</label>
                <DatePicker
                    selected={selectedDate}
                    onChange={handleDateChange}
                    dateFormat="yyyy.MM.dd" // 날짜 형식 지정
                    className="date-picker-input"
                    maxDate={new Date()} // 오늘 날짜까지만 선택 가능
                    showPopperArrow={false}
                />
            </div>

            <div className="chart-section">
                {selectedTab === "week" ? (
                    <div className="chart-placeholder">[주간 차트 표시]</div>
                ) : selectedTab === "month" ? (
                    <div className="chart-placeholder">[월간 차트 표시]</div>
                ) : (
                    <div className="chart-placeholder">[연간 차트 표시]</div>
                )}
            </div>

            <div className="average-temp">
                <p>{`${selectedDate.toISOString().slice(0, 10)}의`}</p>
                <p>
                    평균 온도는 <strong>{averageTemp}</strong> 입니다.
                </p>
            </div>
        </div>
    );
}

export default Statistics;
