import React, { useState } from "react";
import { Line } from "react-chartjs-2";
import "chart.js/auto";
import "./Statistics.css";

const StatsPage = () => {
  const [selectedDate, setSelectedDate] = useState("2024-11-20 ~ 2024-11-27");
  const [averageTemp, setAverageTemp] = useState(25.6);
  const [weeklyData] = useState({
    labels: ["월", "화", "수", "목", "금", "토", "일"],
    datasets: [
      {
        label: "주간 온도",
        data: [10, 15, 25, 20, 30, 35, 40],
        backgroundColor: "rgba(75,192,192,0.4)",
        borderColor: "rgba(75,192,192,1)",
        borderWidth: 2,
        tension: 0.4,
      },
    ],
  });

  return (
    <div className="stats-page">
      <header>
        <h1>FeelsLike</h1>
        <nav>
          <a href="#about">About Us</a>
          <a href="#contact">Contact Us</a>
          <a href="#logout">Logout</a>
          <a href="#mypage">My Page</a>
        </nav>
      </header>
      <main>
        <div className="stats-header">
          <h2>나의 온도 평균</h2>
          <p>내가 그동안 기록해왔던 지난 날을 살펴봐요.</p>
        </div>
        <div className="chart-container">
          <div className="chart-header">
            <button className="tab">주</button>
            <button className="tab">달</button>
            <button className="tab">연도</button>
          </div>
          <Line data={weeklyData} />
          <div className="date-picker">
            <span>날짜 선택: </span>
            <select
              value={selectedDate}
              onChange={(e) => setSelectedDate(e.target.value)}
            >
              <option value="2024-11-20 ~ 2024-11-27">
                2024-11-20 ~ 2024-11-27
              </option>
              <option value="2024-11-13 ~ 2024-11-19">
                2024-11-13 ~ 2024-11-19
              </option>
            </select>
          </div>
          <div className="average-temp">
            <p>
              평균 온도는 <strong>{averageTemp}°C</strong> 입니다.
            </p>
          </div>
        </div>
      </main>
    </div>
  );
};

export default StatsPage;
