import React, { useState } from "react";
import "./Statistics.css";

function Statistics() {
    const [selectedTab, setSelectedTab] = useState("week"); // 주/달/연도 탭
    const [selectedDate, setSelectedDate] = useState("2024.11.18~2024.11.24"); // 기본 날짜 선택
    const [averageTemp, setAverageTemp] = useState("25.6°C"); // 온도 데이터

    const handleTabClick = (tab) => {
        setSelectedTab(tab);
    };

    const handleDateChange = (event) => {
        setSelectedDate(event.target.value); // 모의로 날짜 변경 가능
    };

    return (
        <div className="statistics-container">
            <header className="statistics-header">
                <h1>나의 온도 평균</h1>
                <p>내가 그동안 기록해왔던 지난 날을 살펴봐요.</p>
            </header>

            <div className="tabs">
                <button
                    className={`tab-button ${selectedTab === "week" ? "active" : ""}`}
                    onClick={() => handleTabClick("week")}
                >
                    주
                </button>
                <button
                    className={`tab-button ${selectedTab === "month" ? "active" : ""}`}
                    onClick={() => handleTabClick("month")}
                >
                    달
                </button>
                <button
                    className={`tab-button ${selectedTab === "year" ? "active" : ""}`}
                    onClick={() => handleTabClick("year")}
                >
                    연도
                </button>
            </div>

            <div className="date-selector">
                <label htmlFor="date">날짜 선택:</label>
                <select id="date" value={selectedDate} onChange={handleDateChange}>
                    <option value="2024.11.18~2024.11.24">2024.11.18~2024.11.24</option>
                    <option value="2024.11.11~2024.11.17">2024.11.11~2024.11.17</option>
                </select>
            </div>

            <div className="chart-section">
                {selectedTab === "week" ? (
                    <div className="chart-placeholder">[주간 차트 표시]</div>
                ) : selectedTab === "month" ? (
                    <div className="chart-placeholder">[월간 차트 표시]</div>
                ) : (
                    <div className="chart-placeholder">[연간 차트 표시]</div>
                )}
            </div>

            <div className="average-temp">
                <p>{selectedDate}의</p>
                <p>평균 온도는 <strong>{averageTemp}</strong> 입니다.</p>
            </div>
        </div>
    );
}

export default Statistics;
