import React, { useState, useEffect } from "react";
import { Line } from "react-chartjs-2";
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
} from "chart.js";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import "./Statistics.css";

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

function Statistics() {
    const [selectedTab, setSelectedTab] = useState("week");
    const [startDate, setStartDate] = useState(new Date());
    const [endDate, setEndDate] = useState(new Date());
    const [showCalendar, setShowCalendar] = useState(false);
    const [averageTemp, setAverageTemp] = useState("0");
    const [selectedMonth, setSelectedMonth] = useState(new Date().getMonth() + 1);
    const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());
    const [hasData, setHasData] = useState(true);

    const fetchStatistics = async (date) => {
       try {
            const response = await fetch(`/api/statistic/${date}`, {
                method: 'GET',
                headers: {
                  'Content-Type': 'application/json',
                   // Ïù∏Ï¶ù ÌÜ†ÌÅ∞ÏùÑ Ìó§ÎçîÏóê Ï∂îÍ∞Ä (Ïòà: JWT)
                  'Authorization': `Bearer ${localStorage.getItem('token')}`
                },
              });
              if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
              }
              const data = await response.json();
              return data.data;
            } catch (error) {
              console.error("ÌÜµÍ≥Ñ Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞ Ïò§Î•ò:", error);
              setHasData(false);
              return null;
            }
    };

    useEffect(() => {
        const fetchData = async () => {
            const formattedDate = startDate.toISOString().split('T')[0];
            const statisticsData = await fetchStatistics(formattedDate);
            if (statisticsData) {
                setAverageTemp(statisticsData.temperature.toString());
                setHasData(true);
            }
        };

        fetchData();
    }, [startDate]);

    const handleTabClick = (tab) => {
        setSelectedTab(tab);
        setShowCalendar(false);
        setHasData(tab === "week");
    };

    const handleDateChange = async (dates) => {
        const [start, end] = dates;
        if (start && end) {
            setStartDate(start);
            setEndDate(end);

            const formattedDate = start.toISOString().split('T')[0];
            const statisticsData = await fetchStatistics(formattedDate);
            if (statisticsData) {
                setAverageTemp(statisticsData.temperature.toString());
                setHasData(true);
            }
        } else {
            setHasData(false);
            setAverageTemp("0");
        }
    };

    const weekDays = ['Ïõî', 'Ìôî', 'Ïàò', 'Î™©', 'Í∏à', 'ÌÜ†', 'Ïùº'];

    const data = {
        labels: weekDays,
        datasets: [{
            data: Array(7).fill(parseFloat(averageTemp)),
            borderColor: '#FF9B9B',
            backgroundColor: 'rgba(255, 155, 155, 0.1)',
            fill: true,
            tension: 0,
            pointRadius: 5,
            pointBackgroundColor: ['#4AB7B6', '#A0D995', '#FF9B9B', '#A0D995', '#4AB7B6', '#4AB7B6', '#FF9B9B']
        }]
    };

    const options = {
        responsive: true,
        maintainAspectRatio: false,
        plugins: {
            legend: {
                display: false
            },
            tooltip: {
                enabled: true,
                callbacks: {
                    label: (context) => `${context.parsed.y}¬∞C`
                }
            }
        },
        scales: {
            x: {
                grid: {
                    display: true,
                    drawBorder: false
                },
                ticks: {
                    color: '#666'
                }
            },
            y: {
                display: false,
                min: 0,
                max: 40,
                grid: {
                    display: false
                }
            }
        },
        layout: {
            padding: 10
        }
    };

    const renderDateSelector = () => {
        if (selectedTab === "week") {
            return (
                <button className="date-selector-button" onClick={() => setShowCalendar(!showCalendar)}>
                    ÎÇ†Ïßú ÏÑ†ÌÉù ‚ñº
                </button>
            );
        } else if (selectedTab === "month") {
            return (
                <select 
                    value={selectedMonth}
                    onChange={(e) => setSelectedMonth(parseInt(e.target.value))}
                    className="date-selector-button"
                >
                    {Array.from({length: 12}, (_, i) => i + 1).map(month => (
                        <option key={month} value={month}>{month}Ïõî</option>
                    ))}
                </select>
            );
        } else {
            return (
                <select 
                    value={selectedYear}
                    onChange={(e) => setSelectedYear(parseInt(e.target.value))}
                    className="date-selector-button"
                >
                    {[2022, 2023, 2024].map(year => (
                        <option key={year} value={year}>{year}ÎÖÑ</option>
                    ))}
                </select>
            );
        }
    };

    const formatDateRange = () => {
        if (selectedTab === "week") {
            if (!startDate || !endDate) {
                return "ÎÇ†Ïßú ÎØ∏ÏßÄÏ†ï";
            }
            return `${startDate.getFullYear()}.${String(startDate.getMonth() + 1).padStart(2, '0')}.${String(startDate.getDate()).padStart(2, '0')}~${endDate.getFullYear()}.${String(endDate.getMonth() + 1).padStart(2, '0')}.${String(endDate.getDate()).padStart(2, '0')}`;
        } else if (selectedTab === "month") {
            return `${selectedMonth}Ïõî`;
        } else {
            return `${selectedYear}ÎÖÑÎèÑ`;
        }
    };

    return (
        <div className="statistics-container">
            <header className="statistics-header">
                <h1>ÎÇòÏùò Ïò®ÎèÑ ÌèâÍ∑†</h1>
                <p>ÎÇ¥Í∞Ä Í∑∏ÎèôÏïà Í∏∞Î°ùÌï¥ÏôîÎçò ÏßÄÎÇú ÎÇ†ÏùÑ ÏÇ¥Ìé¥Î¥êÏöî.</p>
            </header>

            <div className="tabs">
                <button
                    className={`tab-button ${selectedTab === "week" ? "active" : ""}`}
                    onClick={() => handleTabClick("week")}
                >
                    Ï£º
                </button>
                <button
                    className={`tab-button ${selectedTab === "month" ? "active" : ""}`}
                    onClick={() => handleTabClick("month")}
                >
                    Îã¨
                </button>
                <button
                    className={`tab-button ${selectedTab === "year" ? "active" : ""}`}
                    onClick={() => handleTabClick("year")}
                >
                    Ïó∞ÎèÑ
                </button>
            </div>

            <div className="mood-indicators">
                <div className="mood-item">
                    <span className="mood-emoji">üò¢</span>
                    <span className="mood-count">: -</span>
                </div>
                <div className="mood-item">
                    <span className="mood-emoji">üòê</span>
                    <span className="mood-count">: -</span>
                </div>
                <div className="mood-item">
                    <span className="mood-emoji">üòä</span>
                    <span className="mood-count">: -</span>
                </div>
                {renderDateSelector()}
            </div>

            {showCalendar && selectedTab === "week" && (
                <div className="calendar-wrapper">
                    <DatePicker
                        selected={startDate}
                        onChange={handleDateChange}
                        startDate={startDate}
                        endDate={endDate}
                        selectsRange
                        inline
                        dateFormat="yyyy.MM.dd"
                    />
                </div>
            )}

            <div className="chart-section">
                {hasData ? (
                    <Line data={data} options={options} />
                ) : (
                    <div className="no-data">Ï†ïÎ≥¥Í∞Ä ÏóÜÏäµÎãàÎã§.</div>
                )}
            </div>

            <div className="result-box">
                <p>
                    {hasData ? (
                        <>
                            <div className="date-range">
                                {selectedTab === "week" && `${formatDateRange()}Ïùò`}
                                {selectedTab === "month" && `ÏÑ†ÌÉùÌïòÏã† ${selectedMonth}ÏõîÏùÄ`}
                                {selectedTab === "year" && `ÏÑ†ÌÉùÌïòÏã† ${selectedYear}ÎÖÑÏùÄ`}
                            </div>
                            <div>ÌèâÍ∑† Ïò®ÎèÑÎäî <span className="temperature">{averageTemp}¬∞C</span> ÏûÖÎãàÎã§.</div>
                        </>
                    ) : (
                        <>
                            <div className="date-range">-</div>
                            <div>ÌèâÍ∑† Ïò®ÎèÑÎäî <span className="temperature">---¬∞C</span> ÏûÖÎãàÎã§.</div>
                        </>
                    )}
                </p>
            </div>
        </div>
    );
}

export default Statistics;
