import React, { useState, useEffect } from "react";
import './Dashboard.css';
import { useNavigate } from "react-router-dom";

function Dashboard() {
    const navigate = useNavigate();
    const [averageTemp, setAverageTemp] = useState(null);
    const [period, setPeriod] = useState("2024.11.18~2024.11.24"); // 초기값

    // 온도 데이터를 주기적으로 가져오는 함수
    const fetchTemperature = async () => {
        try {
            // API 요청 (여기에 실제 API URL을 추가)
            // const response = await fetch('https://api.weather.com/averageTemp');
            // const data = await response.json();

            // 모의 데이터 (임의의 온도 생성)
            const mockData = {
                averageTemp: (Math.random() * 10 + 15).toFixed(1) + "°C",
            };

            setAverageTemp(mockData.averageTemp);
        } catch (error) {
            console.error("온도 데이터를 가져오는 중 오류 발생:", error);
        }
    };

    useEffect(() => {
        // 컴포넌트가 마운트될 때 데이터 가져오기 및 주기적 갱신 설정
        fetchTemperature();
        const interval = setInterval(fetchTemperature, 60000); // 1분마다 갱신

        // 컴포넌트 언마운트 시 인터벌 정리
        return () => clearInterval(interval);
    }, []);

    const handleSoloWrite = () => {
        navigate('/write/solo');
    };

    const handleStatsPage = () => {
        navigate('/api/statistics/{date}'); // 통계 페이지로 이동
    };

    return (
        <div className="dashboard-container">
            <div className="welcome-section">
                <div className="welcome-content">
                    <h2>오늘 하루를 기록해보아요</h2>
                    <p>기록은 당신의 마음과 시간을 연결하는 다이어리입니다.</p>
                    <p>당신의 추억을 기록해줄 FeelsLike</p>
                </div>
            </div>
            <div className="main-content">
                <div className="start-buttons">
                    <div className="start-button" onClick={handleSoloWrite}>
                        <div className="icon-wrapper">
                            <span className="user-icon">👤</span>
                        </div>
                        <div className="button-text">
                            <h3>나 혼자 일기 쓰러가기</h3>
                            <p>나만의 일상 일기와 질문 일기를 작성해요.</p>
                        </div>
                        <span className="arrow">›</span>
                    </div>

                    <div className="start-button">
                        <div className="icon-wrapper">
                            <span className="user-icon">👥</span>
                        </div>
                        <div className="button-text">
                            <h3>친구와 일기 쓰러가기</h3>
                            <p>친구들과 같이 일기를 쓰며 추억을 기록해요.</p>
                        </div>
                        <span className="arrow">›</span>
                    </div>
                </div>
                <div className="stats-section">
                    <div className="stats-header">
                        <h3>나의 온도 평균</h3>
                        <span 
                            className="view-more" 
                            onClick={handleStatsPage}
                        >
                            연도, 월, 주별로 분석하고 싶어요 ›
                        </span>
                    </div>
                    <div className="temperature-display">
                        <span className="period">{period}</span>
                        <span className="temp">이번주 평균 온도 {averageTemp}</span>
                    </div>
                    <div className="graph-container">
                        {/* 그래프 컴포넌트 */}
                    </div>
                </div>

                <div className="bottom-section">
                    <div className="calendar-section">
                        <div className="section-header">
                            <h3>나만의 캘린더</h3>
                            <span className="view-more">자세히 보기 ›</span>
                        </div>
                        {/* 캘린더 컴포넌트 */}
                    </div>
                    <div className="diary-section">
                        <div className="section-header">
                            <h3>나의 일기 기록</h3>
                            <span className="view-more">자세히 보기 ›</span>
                        </div>
                        {/* 일기 기록 컴포넌트 */}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Dashboard;
