import React, { useState, useEffect } from "react";
import './Dashboard.css';
import { useNavigate } from "react-router-dom";

function Dashboard() {
    const navigate = useNavigate();
    const [averageTemp, setAverageTemp] = useState(null);
    const [period, setPeriod] = useState("2024.11.18~2024.11.24"); // Ï¥àÍ∏∞Í∞í

    // Ïò®ÎèÑ Îç∞Ïù¥ÌÑ∞Î•º Ï£ºÍ∏∞Ï†ÅÏúºÎ°ú Í∞ÄÏ†∏Ïò§Îäî Ìï®Ïàò
    const fetchTemperature = async () => {
        try {
            // API ÏöîÏ≤≠ (Ïó¨Í∏∞Ïóê Ïã§Ï†ú API URLÏùÑ Ï∂îÍ∞Ä)
            // const response = await fetch('https://api.weather.com/averageTemp');
            // const data = await response.json();

            // Î™®Ïùò Îç∞Ïù¥ÌÑ∞ (ÏûÑÏùòÏùò Ïò®ÎèÑ ÏÉùÏÑ±)
            const mockData = {
                averageTemp: (Math.random() * 10 + 15).toFixed(1) + "¬∞C",
            };

            setAverageTemp(mockData.averageTemp);
        } catch (error) {
            console.error("Ïò®ÎèÑ Îç∞Ïù¥ÌÑ∞Î•º Í∞ÄÏ†∏Ïò§Îäî Ï§ë Ïò§Î•ò Î∞úÏÉù:", error);
        }
    };

    useEffect(() => {
        // Ïª¥Ìè¨ÎÑåÌä∏Í∞Ä ÎßàÏö¥Ìä∏Îê† Îïå Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞ Î∞è Ï£ºÍ∏∞Ï†Å Í∞±Ïã† ÏÑ§Ï†ï
        fetchTemperature();
        const interval = setInterval(fetchTemperature, 60000); // 1Î∂ÑÎßàÎã§ Í∞±Ïã†

        // Ïª¥Ìè¨ÎÑåÌä∏ Ïñ∏ÎßàÏö¥Ìä∏ Ïãú Ïù∏ÌÑ∞Î≤å Ï†ïÎ¶¨
        return () => clearInterval(interval);
    }, []);

    const handleSoloWrite = () => {
        navigate('/write/solo');
    };

    const handleStatsPage = () => {
        navigate('/api/statistics/{date}'); // ÌÜµÍ≥Ñ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
    };

    return (
        <div className="dashboard-container">
            <div className="welcome-section">
