// Statistics.js
import React, { useState } from "react";
import { Line } from "react-chartjs-2";
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
} from "chart.js";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import "./Statistics.css";

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

function Statistics() {
    const [selectedTab, setSelectedTab] = useState("week");
    const [startDate, setStartDate] = useState(new Date());
    const [endDate, setEndDate] = useState(new Date());
    const [showCalendar, setShowCalendar] = useState(false);
    const [selectedMonth, setSelectedMonth] = useState(new Date().getMonth() + 1);
    const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());
    const [averageTemp, setAverageTemp] = useState("---");

    const handleTabClick = (tab) => {
        setSelectedTab(tab);
        setShowCalendar(false);
    };

    const handleDateChange = (date) => {
        const [start, end] = date;
        setStartDate(start);
        setEndDate(end);
    };

    const formatDateRange = () => {
        if (selectedTab === "week" && startDate && endDate) {
            return `${startDate.getFullYear()}.${String(startDate.getMonth() + 1).padStart(2, '0')}.${String(startDate.getDate()).padStart(2, '0')} ~ ${endDate.getFullYear()}.${String(endDate.getMonth() + 1).padStart(2, '0')}.${String(endDate.getDate()).padStart(2, '0')}`;
        } else if (selectedTab === "month") {
            return `${selectedMonth}ÏõîÏùò Ïò®ÎèÑ`;
        } else {
            return `${selectedYear}ÎÖÑÎèÑÏùò Ïò®ÎèÑ`;
        }
    };

    const renderDateSelector = () => {
        if (selectedTab === "week") {
            return (
                <>
                    <button className="date-selector-button" onClick={() => setShowCalendar(!showCalendar)}>
                        ÎÇ†Ïßú ÏÑ†ÌÉù ‚ñº
                    </button>
                    {showCalendar && (
                        <div className="calendar-wrapper">
                            <DatePicker
                                selected={startDate}
                                onChange={handleDateChange}
                                startDate={startDate}
                                endDate={endDate}
                                selectsRange
                                inline
                                dateFormat="yyyy.MM.dd"
                            />
                        </div>
                    )}
                </>
            );
        } else if (selectedTab === "month") {
            return (
                <select 
                    value={selectedMonth}
                    onChange={(e) => setSelectedMonth(parseInt(e.target.value))}
                    className="month-selector"
                >
                    {Array.from({length: 12}, (_, i) => i + 1).map(month => (
                        <option key={month} value={month}>{month}Ïõî</option>
                    ))}
                </select>
            );
        } else {
            return (
                <select 
                    value={selectedYear}
                    onChange={(e) => setSelectedYear(parseInt(e.target.value))}
                    className="year-selector"
                >
                    {[2022, 2023, 2024].map(year => (
                        <option key={year} value={year}>{year}ÎÖÑ</option>
                    ))}
                </select>
            );
        }
    };

    // JSX ÏàòÏ†ï
    return (
        <div className="statistics-container">
            {/* Í∏∞Ï°¥ Ìó§ÎçîÏôÄ ÌÉ≠ ÏΩîÎìúÎäî ÎèôÏùº */}

            <div className="mood-indicators">
                <div className="mood-item">
                    <span className="mood-emoji">üò¢</span>
                    <span className="mood-count">: -</span>
                </div>
                <div className="mood-item">
                    <span className="mood-emoji">üòê</span>
                    <span className="mood-count">: -</span>
                </div>
                <div className="mood-item">
                    <span className="mood-emoji">üòä</span>
                    <span className="mood-count">: -</span>
                </div>
                {renderDateSelector()}
            </div>

            <div className="chart-section">
                <Line data={data} options={options} />
            </div>

            <div className="result-box">
                <p>ÌèâÍ∑† Ïò®ÎèÑÎäî <span className="temperature">{averageTemp}¬∞C</span> ÏûÖÎãàÎã§.</p>
                <p className="date-range">{formatDateRange()}</p>
            </div>
        </div>
    );
}

export default Statistics;
